import React, { useState, useEffect, Component } from 'react';
// import './components/Header.css';
// import { CSSTransition } from 'react-transition-group';
import burger from '../src/assets/burger.png'

// import Header from './components/Header';
// import SimpleSlider from './components/corusel'
import {Navbar, Nav, NavItem,NavDropdown,Form,FormControl,Button} from 'react-bootstrap';
import logo from './assets/logo.png';

class App extends Component {
  constructor(props) {
    super(props);
 
    this.state = {
      isShow: true,
    };
    this.state = {
      list: [{
          text01:"В мире есть самые современные технологии…",
          text02:"эффективные разработки...",
          text03:"и есть надежные партнеры",


        text1: "Многие из Вас сегодня впервые узнают о нашей компании, а те, кто сотрудничает с нами, возможно, еще раз убедятся в правильности сделанного выбора",

        text2: "За время своего существования, мы добились определенных успехов и достижений, расширили рынок предложения своих услуг. Границы нашего сотрудничества сейчас охватывают круг как отечественных, так и зарубежных партнеров.",
          
        text3: "Инициатива, увлеченность, квалифицированный персонал, преданность компании и забота об интересах заказчика – вот ключ к достижению поставленных целей.",
          
        text4: "Приглашаем Вас ознакомиться с нашим опытом и производственными ресурсами и выражаем уверенность, что «Инженерная фирма MG Engineering» может быть полезна в успешной реализации запланированных Вами задач.",
          
        text5: "Мы открыты для сотрудничества со всеми заинтересованными компаниями и будем оказывать всемерную поддержку в построении и укреплении взаимовыгодных партнерских отношений.",
          
        text6: "С наилучшими пожеланиями успехов в бизнесе,",
          
        text7: "Коллектив «Инженерной фирмы MG Engineering»"

      }, {
        text01:"Әлемде ең",
        text02:"заманауи технологиялар...",
        text03:"тиімді талдамалар...",
        text04:" және сенімді серіктестер бар",


        text1: "Сіздердің көбіңіз бүгін біздің компаниямыз туралы алғаш рет естиді, ал бізбен ынтымақтасып келе жатқан адамдар, мүмкін, жасаған таңдауының дұрыс екеніне тағы да бір рет көз жеткізер.",

        text2: "Жұмыс істеген уақыт ішінде біз белгілі бір табыстар мен жетістіктерге қол жеткіздік, өз қызметтерімізді ұсынатын нарықты кеңейттік. Біздің ынтымақтастығымыздың шекаралары қазір отандық серіктестерді де, шетелдік серіктестерді де қамтиды.",
        
        text3: "Ынтамыз, жұмысқа берілгендігіміз, білікті қызметкерлеріміз, компанияға деген адалдығымыз бен тапсырыс берушінің мүдделеріне қамқорлық жасау – алға қойылған мақсаттарға қол жеткізудің жолы, міне, осы.",
        
        text4: "Сіздерді біздің тәжірибемізбен және өндірістік ресурстарымызбен танысуға шақырамыз және «MG Engineering инженерлік фирмасы» Сіздердің алға қойған міндеттеріңізді табысты жүзеге асыруда пайдалы болатынына сенім білдіреміз.",
        
        text5: "Біз барлық қызығушылық танытқан компаниялармен ынтымақтасуға дайынбыз және өзара пайдалы серіктестік қарым-қатынастарды құруда және оларды нығайтуда барынша қолдау көрсететін боламыз.",
        
        text6: "Бизнесте табысқа қол жеткізіңіздер деген ақ тілектермен",
        
        text7: "«MG Engineering инженерлік фирмасы» ұжымы"
      }, {
        text01:"The most up-to-date technologies...",
        text02:"efficient solutions...",
        text03:"and reliable partners are",
        text04:"available in the world",
           
  
  
        text1: "Today, many of you will get wise to our company for the first time, and those who cooperates with us will probably realize the right choice made one more time.",
  
        text2: "During the existence of our company, we have succeeded in getting certain achievements with the market of our services being expanded. The boundaries of our cooperation cover both local and foreign partners.",
        
        text3: "Initiative, dedication, qualified personnel, attachment to the company and customer interests care are the key to achieve the goals desired.",

        text4: "Please refer to our experience and production resources. We are expressing our assurance that MG Engineering Company can be helpful in successful implementation of the tasks you scheduled.",
        
        text5: "We are open for cooperation with all interested companies and will fully cooperate in building and empowering mutually beneficial partner relations.",
        
        text6: "With the best wishes in success in business,",
        
        text7: "MG Engineering Company Team"
      }],
      currentIndex: 0
    }
  }

  myFunction() {
  var x = document.getElementById("main-nav");
  if (x.style.display === "grid") {
    x.style.display = "none";
  } else {
    x.style.display = "grid";
  }
  }
 
  toggleShow = () => {
    this.setState(state => ({ isShow: !state.isShow }));
  };
 
  render() {
    const greeting = 'Welcome to React';
 
    return (
      <div class="container">
           <Navbar bg="light" expand="lg">
            <Navbar.Brand href="#home"><img src={logo}/></Navbar.Brand>
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav">
              <Nav className="mr-auto">
              		
                <Nav.Link href="\#">Главная</Nav.Link>
                <Nav.Link href="#intro">О компании</Nav.Link>
                <Nav.Link href="#work">Деятельность</Nav.Link>
                <Nav.Link href="#about">Проекты</Nav.Link>
                <Nav.Link href="#contact">Управление</Nav.Link>
                <Nav.Link href="\Certificates">Сертификаты и лицензии</Nav.Link>
                <Nav.Link href="\contacts">Контакты</Nav.Link>
                <Nav.Link href="#link"></Nav.Link>
                <NavDropdown title="Выбор языка"id="basic-nav-dropdown">
                  <NavDropdown.Item href="#action/3.1"  > 
                  <button onClick={
                        () => {
                          this.setState({ currentIndex: 0 })
                        }
                      }>RU</button></NavDropdown.Item>
                  <NavDropdown.Item href="#action/3.2"> <button onClick={
                        () => {
                          this.setState({ currentIndex: 1})
                        }
                      }>KZ</button></NavDropdown.Item>
                  <NavDropdown.Item href="#action/3.3"> <button onClick={
                        () => {
                          this.setState({ currentIndex: 2 })
                        }
                      }>EN</button></NavDropdown.Item>
                  <NavDropdown.Divider />
                  {/* <NavDropdown.Item href="#action/3.4">Separated link</NavDropdown.Item> */}
                </NavDropdown>
              </Nav>
             
               
                     
            </Navbar.Collapse>
          </Navbar>
      <div class="">
    
          <div class="">
             
          </div>
          {/* <button className="" onClick={this.toggleMenu} type="button">
                                <i className="mdi mdi-menu"></i>
                            </button> */}
          <div >
       
              {/* <ul>
              <a href="/">Главна11111111я</a>
              <a href="/">О компании</a>
              <a href="/">Деятельность</a>
              <a href="/">Проекты</a>
              <a href="/">Управление</a>
              <a href="/">Сертификаты и лицензии</a>
              <a href="/">Контакты</a>
              <button onClick={
                        () => {
                          this.setState({ currentIndex: 0 })
                        }
                      }>hello</button>
                <button onClick={
                        () => {
                          this.setState({ currentIndex: 1})
                        }
                      }>every</button>
                      <button onClick={
                        () => {
                          this.setState({ currentIndex: 2 })
                        }
                      }>body</button>
              </ul> */}
          </div>
          {/* <div class="hamburger">
              <a href="javascript:void(0);" class="icon" onclick=" myFunction()">
                  <img src={burger} width='30px' height='30px'/>
              </a>
          </div> */}
      </div>

          


{/* <nav id="main-nav" class="main-nav">
          <a href="/">Главная</a>
          <a href="/">О компании</a>
          <a href="/">Деятельность</a>
          <a href="/">Проекты</a>
          <a href="/">Управление</a>
          <a href="/">Сертификаты и лицензии</a>
          <a href="/">Контакты</a>
      
       
          <button onClick={
        () => {
          this.setState({ currentIndex: 0 })
        }
      }>hello</button>
<button onClick={
        () => {
          this.setState({ currentIndex: 1})
        }
      }>every</button>
       <button onClick={
        () => {
          this.setState({ currentIndex: 2 })
        }
      }>body</button>
   

   <div class="hamburger">
                    <a href="javascript:void(0);" class="icon" onclick="myFunction()">
                        <i class="fa fa-bars"></i>
                    </a>
                </div>

        </nav>  */}
		<div className="Content">
      
      <div id='contact' >
      <h1>block1</h1>
        <Greeting greeting={greeting} isShow={this.state.isShow} />
        <h2>{ this.state.list[this.state.currentIndex].text01}</h2>
        <h2>{ this.state.list[this.state.currentIndex].text02}</h2>
        <h2>{ this.state.list[this.state.currentIndex].text03}</h2>
        <h2>{ this.state.list[this.state.currentIndex].text4}</h2>
        <h2>{ this.state.list[this.state.currentIndex].text5}</h2>
        <h2>{ this.state.list[this.state.currentIndex].text6}</h2>
