import '../src/components/Header.css';
// import Header from './components/Header';
import SimpleSlider from './components/corusel'

import React, { useState, useEffect } from 'react';
import './components/Header.css';
import { CSSTransition } from 'react-transition-group';
import burger from '../src/assets/burger.png'



 



function App() {
  
  
  state = {
    list: [{
      image: "olio",
      nom: "live",
      des:"live is short",
    }, {
      image:" {jiko}",
      nom: "for",
      des:"inspire and breath",
    }, {
      nom: "everything",
      des:"this is great",
      image: " {olio}",
    }],
    currentIndex: 0
  }

  const [isNavVisible, setNavVisibility] = useState(false);
  const [isSmallScreen, setIsSmallScreen] = useState(false);

  useEffect(() => {
    const mediaQuery = window.matchMedia('(max-width: 700px)');
    mediaQuery.addListener(handleMediaQueryChange);
    handleMediaQueryChange(mediaQuery);

    return () => {
      mediaQuery.removeListener(handleMediaQueryChange);
    };
  }, []);

  const handleMediaQueryChange = mediaQuery => {
    if (mediaQuery.matches) {
      setIsSmallScreen(true);
    } else {
      setIsSmallScreen(false);
    }
  };

  const toggleNav = () => {
    setNavVisibility(!isNavVisible);
  };

  return (
    
    <div className="App">
      <header className="Header">
      <img src={require('../src/assets/logo.png')} className="Logo" alt="logo" />
      <CSSTransition
        in={!isSmallScreen || isNavVisible}
        timeout={350}
        classNames="NavAnimation"
        unmountOnExit
      >
        <nav className="Nav">
          <a href="/">Главная</a>
          <a href="/">О компании</a>
          <a href="/">Деятельность</a>
          <a href="/">Проекты</a>
          <a href="/">Управление</a>
          <a href="/">Сертификаты и лицензии</a>
          <a href="/">Контакты</a>
          <button onClick={
                                                                () => {
                                                                this.setState({ currentIndex: 0 })
                                                                }
                                                            }>hello</button>
                                                        <button onClick={
                                                                () => {
                                                                this.setState({ currentIndex: 1})
                                                                }
                                                            }>every</button>
                                                            <button onClick={
                                                                () => {
                                                                this.setState({ currentIndex: 2 })
                                                                }
                                                            }>body</button>

         
   



        </nav>
      </CSSTransition>
      <button onClick={toggleNav} className="Burger">
        <span role="img">  <img src={burger} width='30px' height='30px'></img> </span>
      </button>
    </header>
      {/* <SimpleSlider/> */}
      <div className="Content">
      
      <img src={ this.state.list[this.state.currentIndex].image} style = {{height : "500px", width : "400px"}}/>
      
      <h3  style = {{backgroundColor:"black",color:"white",fontFamily:"monospace",}}>{ this.state.list[this.state.currentIndex].nom}</h3>
      
     <h2>{ this.state.list[this.state.currentIndex].des}</h2>                                                    
        <p>42 is the answer to everything.sssssssssssssssssssssssssssssssssssssssssssss..</p>
      </div>
    </div>
  );
                                                          }
export default App;
