import React, { useState, useEffect } from 'react';
import './Header.css';
import { CSSTransition } from 'react-transition-group';

export default function Header() {
  const [isNavVisible, setNavVisibility] = useState(false);
  const [isSmallScreen, setIsSmallScreen] = useState(false);

  useEffect(() => {
    const mediaQuery = window.matchMedia('(max-width: 700px)');
    mediaQuery.addListener(handleMediaQueryChange);
    handleMediaQueryChange(mediaQuery);

    return () => {
      mediaQuery.removeListener(handleMediaQueryChange);
    };
  }, []);

  const handleMediaQueryChange = mediaQuery => {
    if (mediaQuery.matches) {
      setIsSmallScreen(true);
    } else {
      setIsSmallScreen(false);
    }
  };

  const toggleNav = () => {
    setNavVisibility(!isNavVisible);
  };

  return (
    <header className="Header">
      <img src={require('../assets/logo.png')} className="Logo" alt="logo" />
      <CSSTransition
        in={!isSmallScreen || isNavVisible}
        timeout={350}
        classNames="NavAnimation"
        unmountOnExit
      >
        <nav className="Nav">
          <a href="/">Home</a>
          <a href="/">Articles</a>
          <a href="/">About</a>
          <a href="/">Logout</a>
         
         
          <ul className={this.state.isOpen ? "navbar-nav navbar-left" : "navbar-nav ml-auto navbar-left"} id="mySidenav">
                                        <li className="nav-item active"><a href="#home" className="nav-link">–ì–õ–ê–í–ù–ê–Ø</a></li>
                                        <li className="nav-item"><a href="#features" className="nav-link">–û –ö–û–ú–ü–ê–ù–ò–ò</a></li>
                                        <li className="nav-item"><a href="#pricing" className="nav-link">–î–ï–Ø–¢–ï–õ–¨–ù–û–°–¢–¨</a> </li>
                                        {/* <li className="nav-item"><a href="#aboutCompany" className="nav-link">–û –∫–æ–º–ø–∞–Ω–∏–∏2</a></li> */}
                                        <li className="nav-item"><a href="#services" className="nav-link">–ü–†–û–ï–ö–¢–´</a></li>
                                        <li className="nav-item"><a href="#about" className="nav-link">–£–ü–†–ê–í–õ–ï–ù–ò–ï</a></li>
                                       
                                        <li className="nav-item"><a href="#blog" className="nav-link">–°–ï–†–¢–ò–§–ò–ö–ê–¢–´ –ò –õ–ò–¶–ï–ù–ó–ò–ò</a></li>
                                        <li className="nav-item"><a href="#contact" className="nav-link">–ö–û–ù–¢–ê–ö–¢–´</a> </li>
                                   
                                        <li>   <button onClick={
                                                                () => {
                                                                this.setState({ currentIndex: 0 })
                                                                }
                                                            }>hello</button>
                                                        <button onClick={
                                                                () => {
                                                                this.setState({ currentIndex: 1})
                                                                }
                                                            }>every</button>
                                                            <button onClick={
                                                                () => {
                                                                this.setState({ currentIndex: 2 })
                                                                }
                                                            }>body</button></li>
                                    </ul>



        </nav>
      </CSSTransition>
      <button onClick={toggleNav} className="Burger">
        <span role="img"> üçî </span>
      </button>
    </header>
  );
}
