import React, { useState, useEffect, Component } from 'react';
// import './components/Header.css';
// import { CSSTransition } from 'react-transition-group';
import burger from '../src/assets/burger.png'

// import Header from './components/Header';
// import SimpleSlider from './components/corusel'
import {Navbar, Nav, NavItem,NavDropdown,Form,FormControl,Button} from 'react-bootstrap';


class App extends Component {
  constructor(props) {
    super(props);
 
    this.state = {
      isShow: true,
    };
    this.state = {
      list: [{
        image: "olio",
        nom: "live",
        text1:"live is short",
      }, {
        image:" {jiko}",
        nom: "for",
        text1:"inspire and breath",
      }, {
        nom: "everything",
        text1:"this is great",
        image: " {olio}",
      }],
      currentIndex: 0
    }
  }

  myFunction() {
  var x = document.getElementById("main-nav");
  if (x.style.display === "grid") {
    x.style.display = "none";
  } else {
    x.style.display = "grid";
  }
  }
 
  toggleShow = () => {
    this.setState(state => ({ isShow: !state.isShow }));
  };
 
  render() {
    const greeting = 'Welcome to React';
 
    return (
      <div class="container">
      <div class="">
    
          <div class="">
              <img src="https://drive.google.com/uc?id=1Uld-9gdvYESuLVDr6O9qd9ZXXunJDABq" alt="Logo"/>
          </div>
          <button className="" onClick={this.toggleMenu} type="button">
                                <i className="mdi mdi-menu"></i>
                            </button>
          <div id="main-nav" class="main-nav">
          <Navbar bg="light" expand="lg">
            <Navbar.Brand href="#home">React-Bootstrap</Navbar.Brand>
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav">
              <Nav className="mr-auto">
                <Nav.Link href="#home">Home</Nav.Link>
                <Nav.Link href="#link">Link</Nav.Link>
                <NavDropdown title="Dropdown" id="basic-nav-dropdown">
                  <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
                  <NavDropdown.Item href="#action/3.2">Another action</NavDropdown.Item>
                  <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
                  <NavDropdown.Divider />
                  <NavDropdown.Item href="#action/3.4">Separated link</NavDropdown.Item>
                </NavDropdown>
              </Nav>
              <Form inline>
                <FormControl type="text" placeholder="Search" className="mr-sm-2" />
                <Button variant="outline-success">Search</Button>
              </Form>
            </Navbar.Collapse>
          </Navbar>
              {/* <ul>
              <a href="/">Главна11111111я</a>
              <a href="/">О компании</a>
              <a href="/">Деятельность</a>
              <a href="/">Проекты</a>
              <a href="/">Управление</a>
              <a href="/">Сертификаты и лицензии</a>
              <a href="/">Контакты</a>
              <button onClick={
                        () => {
                          this.setState({ currentIndex: 0 })
                        }
                      }>hello</button>
                <button onClick={
                        () => {
                          this.setState({ currentIndex: 1})
                        }
                      }>every</button>
                      <button onClick={
                        () => {
                          this.setState({ currentIndex: 2 })
                        }
                      }>body</button>
              </ul> */}
          </div>
          <div class="hamburger">
              <a href="javascript:void(0);" class="icon" onclick=" myFunction()">
                  <img src={burger} width='30px' height='30px'/>
              </a>
          </div>
      </div>

          


{/* <nav id="main-nav" class="main-nav">
          <a href="/">Главная</a>
          <a href="/">О компании</a>
          <a href="/">Деятельность</a>
          <a href="/">Проекты</a>
          <a href="/">Управление</a>
          <a href="/">Сертификаты и лицензии</a>
          <a href="/">Контакты</a>
      
       
          <button onClick={
        () => {
          this.setState({ currentIndex: 0 })
        }
      }>hello</button>
<button onClick={
        () => {
          this.setState({ currentIndex: 1})
        }
      }>every</button>
       <button onClick={
        () => {
          this.setState({ currentIndex: 2 })
        }
      }>body</button>
   

   <div class="hamburger">
                    <a href="javascript:void(0);" class="icon" onclick="myFunction()">
                        <i class="fa fa-bars"></i>
                    </a>
                </div>

        </nav>  */}
		<div className="Content">
        <Greeting greeting={greeting} isShow={this.state.isShow} />
        <h2>{ this.state.list[this.state.currentIndex].text1}</h2>
        {/* <button onClick={this.toggleShow} type="button">
          Toggle Show
        </button> */}
        
      </div>
      </div>
      
    );
  }
}
 
const Greeting = ({ greeting, isShow }) =>
  isShow ? <h1>{greeting}</h1> : null;
 
export default App;
