import '../src/components/Header.css';
// import Header from './components/Header';




import { CSSTransition } from 'react-transition-group';
import burger from '../src/assets/burger.png'

import '../src/components/Header.css';
// import Header from './components/Header';
import SimpleSlider from './components/corusel'

import React, { useState, useEffect, Component } from 'react';
class App extends Component {
  constructor(props) {
    super(props);
 
    this.state = {
      isShow: true,
    };
    this.state = {
      list: [{
        image: "olio",
        nom: "live",
        des:"live is short",
      }, {
        image:" {jiko}",
        nom: "for",
        des:"inspire and breath",
      }, {
        nom: "everything",
        des:"this is great",
        image: " {olio}",
      }],
      currentIndex: 0
    }
  }
 
  toggleShow = () => {
    this.setState(state => ({ isShow: !state.isShow }));
  };
 
  render() {
    const greeting = 'Welcome to React';
 
    return (
      <div className="App"> 
      
      


		<div className="Content">
        <Greeting greeting={greeting} isShow={this.state.isShow} />
        <h2>{ this.state.list[this.state.currentIndex].des}</h2>
        <button onClick={this.toggleShow} type="button">
          Toggle Show
        </button>
        <button onClick={
        () => {
          this.setState({ currentIndex: 0 })
        }
      }>hello</button>
<button onClick={
        () => {
          this.setState({ currentIndex: 1})
        }
      }>every</button>
       <button onClick={
        () => {
          this.setState({ currentIndex: 2 })
        }
      }>body</button>
      </div>
      </div>
    );
  }
}
 
const Greeting = ({ greeting, isShow }) =>
  isShow ? <h1>{greeting}</h1> : null;
 
export default App;
